import { Component } from '@angular/core';
import { Template } from '../template';
import { TaskManagementService } from '../task-management.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-view-task',
  templateUrl: './view-task.component.html',
  styleUrls: ['./view-task.component.css']
})
export class ViewTaskComponent {
  Arr:Template[]=[];
  constructor(public sv:TaskManagementService,public rt:Router){
    this.sv.viewAllTask().subscribe((ts:any)=>{
      this.Arr=ts;
    });
  }
  deleteTask(id:any){
    this.sv.deleteTask(id).subscribe(()=>{
      this.rt.navigate(["/viewTask"]);
    });
  }
}
<header><span>Task Scheluder</span></header>
<main>
  <span *ngIf="Arr.length>0 Then printtask else notprinttask"></span>
  <ng-template #printtask>
    <div *ngFor="let a of Arr">
      <aside>
        <h2>{{a.title}}</h2>
        <p>{{a.date|date}}</p>
        <p>{{a.description}}</p>
      </aside>
      <aside>
        <a (click)="deleteTask(a.id)" class="btn btn-danger">&#10005;&nbsp;Delete</a>
        <a routerLink="/updateTask/{{a.id}}" class="btn btn-info">&#9998;&nbsp;Edit</a>
      </aside>
    </div>
  </ng-template>
  <ng-template #notprinttask>
      <h1>&#10005;&nbsp;No Available Task</h1>
  </ng-template>
  <a class="btn btn-dark" routerLink="/createTask">&#43;&nbsp;Create Task</a>
</main>
