import { Component } from '@angular/core';
import { Template } from '../template';
import { TaskManagementService } from '../task-management.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-view-task',
  templateUrl: './view-task.component.html',
  styleUrls: ['./view-task.component.css']
})
export class ViewTaskComponent {
  Arr:Template[]=[];
  constructor(public sv:TaskManagementService,public rt:Router){
    this.sv.viewAllTask().subscribe((ts:any)=>{
      this.Arr=ts;
    });
  }
  deleteTask(id:any){
    this.sv.deleteTask(id).subscribe(()=>{
      this.rt.navigate(["/viewTask"]);
    });
  }
  togsec(){
    let maincnt=document.getElementById('maincnt');
    let abtcnt=document.getElementById('abtcnt');
    let togbtn=document.getElementById('togbtn');
    maincnt?.classList.toggle('hide');
    abtcnt?.classList.toggle('show');
    togbtn?.classList.toggle('hide');
  }
}
<header>
<span>Task Scheluder</span>
<button (click)="togsec()" id="togbtn">About</button>
</header>
<main id="maincnt">
  <span *ngIf="Arr.length>0 Then printtask else notprinttask"></span>
  <ng-template #printtask>
    <div *ngFor="let a of Arr">
      <aside>
        <h2>{{a.title}}</h2>
        <p>{{a.date|date}}</p>
        <p>{{a.description}}</p>
      </aside>
      <aside>
        <a (click)="deleteTask(a.id)" class="btn btn-danger">&#10005;&nbsp;Delete</a>
        <a routerLink="/updateTask/{{a.id}}" class="btn btn-info">&#9998;&nbsp;Edit</a>
      </aside>
    </div>
  </ng-template>
  <ng-template #notprinttask>
      <h1>&#10005;&nbsp;No Available Task</h1>
  </ng-template>
  <a class="btn btn-dark" routerLink="/createTask">&#43;&nbsp;Create Task</a>
</main>

<article id="abtcnt" class="hide">
  <span (click)="togsec()">&#10005;</span>
  <h2>About Task Scheduler</h2>
  <p>TaskScheduler helps you manage your tasks efficiently by providing features such as creating, editing, and deleting tasks.</p>
  <p>Follow the steps below to create a task:</p>
  <ol>
    <li>Click on the "Create Task" button.</li>
    <li>Fill in the details for your task, including the title, date, and description.</li>
    <li>Click "Add Task" to add the task to your list.</li>
  </ol>
</article>
